import { Button, Image, Skeleton, Typography, message } from "antd";
import ReactPlayer from "react-player";

import { useGetCameraHLSQuery } from "../../store/api/cameraApi";
import { useOpenIntercomMutation } from "../../store/api/intercomApi";
import { classNames } from "../../utils/classNames";

interface IntercomProps {
  open: string;
  preview: string;
  name: string;
  camera: string;
  camera_stable: string;
  className: string;
}
const Intercom = ({
  open,
  preview,
  name,
  camera,
  camera_stable,
  className,
}: IntercomProps): JSX.Element => {
  const {
    data: cameraData,
    isLoading: cameraIsLoading,
    isError: cameraIsError,
  } = useGetCameraHLSQuery({ url: camera });
  const [messageApi, contextHolder] = message.useMessage();
  const [openIntercom, { isLoading }] = useOpenIntercomMutation();

  const openIntercomHandler = async () => {
    try {
      const result = await openIntercom({ url: open }).unwrap();

      if (result.error === 0) {
        messageApi.open({
          type: "error",
          content: result.message,
        });
      } else if (result.error === 1) {
        messageApi.open({
          type: "success",
          content: result.message,
        });
      }
    } catch (e) {
      messageApi.open({
        type: "error",
        content: "Произошла ошибка сервера (42)",
      });
    }
  };

  return (
    <>
      {contextHolder}
      <div
        className={classNames({
          cls: className,
          additional: [
            "flex",
            "flex-col",
            "justify-between",
            "shadow-lg",
            "p-6",
            "gap-3",
          ],
        })}
      >
        <Image
          src={preview}
          alt={name}
          preview={
            !cameraIsLoading && !cameraIsError && cameraData
              ? {
                  imageRender: () => {
                    return (
                      <ReactPlayer
                        playing={true}
                        url={cameraData}
                        controls={true}
                        muted={true}
                        config={{
                          file: {
                            forceHLS: true,
                          },
                        }}
                      />
                    );
                  },
                  onVisibleChange: (visible) => {
                    //TODO Очень грубый костыль, из-за того что при закрытии не пропадает буферизация видео
                    if (!visible) {
                      window.location.reload();
                    }
                  },
                  toolbarRender: () => (
                    <div>
                      <Button href={camera_stable} target="_blank">
                        Более стабильная версия камеры
                      </Button>
                    </div>
                  ),
                }
              : false
          }
          className="max-h-52 object-cover"
          fallback="data:image/png;base64,"
        />

        <div className="flex flex-col gap-3">
          <Typography.Text className="text-center">{name}</Typography.Text>
          <Button
            type="primary"
            loading={isLoading}
            onClick={openIntercomHandler}
          >
            Открыть
          </Button>
        </div>
      </div>
    </>
  );
};

Intercom.Skeleton = ({ className }: { className: string }) => {
  return (
    <div
      className={classNames({
        cls: className,
        additional: ["flex flex-col gap-3 p-6 shadow-lg"],
      })}
    >
      <Skeleton.Image active={true} />
      <Skeleton active={true} />
      <Skeleton.Button active={true} />
    </div>
  );
};

export default Intercom;
